public class Part2 {
    public double cgRatio(String dna){
        int len = dna.length();
        int cgcount = 0;
        dna = dna.toUpperCase();
        for(int i = 0; i < len; i++){
            if(dna.charAt(i) == 'C' || dna.charAt(i) == 'G'){
                cgcount += 1;
            }
        }
        return (double)cgcount / len;
    }
    public int countCTG(String dna){
        int count = 0;
        dna = dna.toUpperCase();
        int pos = dna.indexOf("CTG");
        while(pos != -1){
            count += 1;
            pos = dna.indexOf("CTG", pos + 2);
        }
        return count;
        
    }
    public void testCGRatio(){
        System.out.println(countCTG(""));
        
    }

}
