public class Part3 {
    public int findStopCodon(String dna, int startIndex, String stopCodon){
        int pos = dna.indexOf(stopCodon, startIndex + 3);
        while(pos != -1){
            if((pos - startIndex) % 3 == 0){
                return pos;
            }
            else{
                pos = dna.indexOf(stopCodon, pos + 1);
            }
        }
        return dna.length();
    }
    
    public String findGene(String dna){

        int startIndex = dna.indexOf("ATG");
        if(startIndex == -1)
            return "";
        int taaIndex = findStopCodon(dna, startIndex, "TAA");
        int tagIndex = findStopCodon(dna, startIndex, "TAG");
        int tgaIndex = findStopCodon(dna, startIndex, "TGA");
        int minIndex = Math.min(taaIndex, Math.min(tagIndex, tgaIndex));
        if(minIndex == dna.length())
            return "";
        return dna.substring(startIndex, minIndex + 3);
        
    }
    
    public double cgRatio(String dna){
        int len = dna.length();
        int cgcount = 0;
        dna = dna.toUpperCase();
        for(int i = 0; i < len; i++){
            if(dna.charAt(i) == 'C' || dna.charAt(i) == 'G'){
                cgcount += 1;
            }
        }
        return (double)cgcount / len;
    }
    
    public int printLongerThan60(StorageResource sr){
        int count = 0;
        for(String s: sr.data()){
            if(s.length() > 60)
            {
                //System.out.println(s);
                count += 1;
            }
        }
        return count;
    }
    
    public int printHigherCG(StorageResource sr){
        int count = 0;
        for(String s: sr.data())
        {
            if(cgRatio(s) > 0.35)
            {
                //System.out.println(s);
                count += 1;
            }
        }
        return count;
    }
    
    public void printLongestLength(StorageResource sr){
        int longest = 0;
        for(String s: sr.data()){
            if(s.length() > longest)
                longest = s.length();
        }
        System.out.println("Length of longest string : " + longest);
    }
    
    public StorageResource getAllGenes(String dna){
        StorageResource sr = new StorageResource();
        int count = 0;
        String gene = findGene(dna);
        while(true){
            if(gene.isEmpty())
                break;
            count += 1;
            sr.add(gene);
            dna = dna.substring(dna.indexOf(gene) + gene.length());
            gene = findGene(dna);
        }
        System.out.println("Number of genes : " + count);
        return sr;
    }
    
    public void processGenes(StorageResource sr){
        System.out.println("Number of strings longer than 60 : " + printLongerThan60(sr));
        System.out.println("Number of strings with higher cgratio : " + printHigherCG(sr));
        printLongestLength(sr);
        
        
    }
    
    public void testProcessGenes(){
        FileResource fr = new FileResource("brca1line.fa");
        String dna = fr.asString();
        dna = "";
        dna = dna.toUpperCase();
        StorageResource sr = getAllGenes(dna);
        processGenes(sr);
        
    }
    

}
